select
        id as id,
        goodsId as goodsId,
        itemLink as itemLink,
        title as title,
        dtitle as dtitle,
        desc as desc,
        cid as cid,
        subcid as subcid,
        tbcid as tbcid,
        mainPic as mainPic,
        marketingMainPic as marketingMainPic,
        originalPrice as originalPrice,
        actualPrice as actualPrice,
        discounts as discounts,
        commissionType as commissionType,
        commissionRate as commissionRate,
        couponLink as couponLink,
        couponTotalNum as couponTotalNum,
        couponReceiveNum as couponReceiveNum,
        couponEndTime as couponEndTime,
        couponStartTime as couponStartTime,
        couponPrice as couponPrice,
        couponConditions as couponConditions,
        monthSales as monthSales,
        twoHoursSales as twoHoursSales,
        dailySales as dailySales,
        brand as brand,
        brandId as brandId,
        brandName as brandName,
        createTime as createTime,
        tchaoshi as tchaoshi,
