        activityStartTime as activityStartTime,
        activityEndTime as activityEndTime,
        shopType as shopType,
        haitao as haitao,
        goldSellers as goldSellers,
        sellerId as sellerId,
        shopName as shopName,
        shopLevel as shopLevel,
        descScore as descScore,
        dsrScore as dsrScore,
        dsrPercent as dsrPercent,
        shipScore as shipScore,
        shipPercent as shipPercent,
        serviceScore as serviceScore,
        servicePercent as servicePercent,
        hotPush as hotPush,
        teamName as teamName
from goods


            and id=#{id}


            and goodsId=#{goodsId}


            and itemLink=#{itemLink}


            and title=#{title}


            and dtitle=#{dtitle}


            and desc=#{desc}


            and cid=#{cid}


            and subcid=#{subcid}


            and tbcid=#{tbcid}


            and mainPic=#{mainPic}


            and marketingMainPic=#{marketingMainPic}


            and originalPrice=#{originalPrice}


            and actualPrice=#{actualPrice}


            and discounts=#{discounts}


            and commissionType=#{commissionType}


            and commissionRate=#{commissionRate}


            and couponLink=#{couponLink}


            and couponTotalNum=#{couponTotalNum}


            and couponReceiveNum=#{couponReceiveNum}


            and couponEndTime=#{couponEndTime}


            and couponStartTime=#{couponStartTime}


            and couponPrice=#{couponPrice}


            and couponConditions=#{couponConditions}


            and monthSales=#{monthSales}


            and twoHoursSales=#{twoHoursSales}


            and dailySales=#{dailySales}


            and brand=#{brand}


            and brandId=#{brandId}


            and brandName=#{brandName}


            and createTime=#{createTime}


            and tchaoshi=#{tchaoshi}


            and activityType=#{activityType}


            and activityStartTime=#{activityStartTime}


