            and shopName=#{shopName}


            and shopLevel=#{shopLevel}


            and descScore=#{descScore}


            and dsrScore=#{dsrScore}


            and dsrPercent=#{dsrPercent}


            and shipScore=#{shipScore}


            and shipPercent=#{shipPercent}


            and serviceScore=#{serviceScore}


            and servicePercent=#{servicePercent}


            and hotPush=#{hotPush}


            and teamName=#{teamName}



    limit #{beginPos},#{pageSize}

